import React, {useEffect, useState} from 'react';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import {Actions} from '../actions';
import './ProductList.css';
import { fade, makeStyles } from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import Pagination from '@material-ui/lab/Pagination';

const defaultImage = "data:image/png;base64,";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        www.doaquafishes.com
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: "#cccccc29",
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: "#cccccc29",
    padding: theme.spacing(6),
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      // width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
}));


export default function ProductList() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const productList = useSelector(state => state.list.list);
  const [page, setPage] = useState(1);
  const [searchText, setSearch] = useState('');

  const handleChange = (event, value) => {
    setPage(value);
  };

  useEffect(()=>{
    getProducts();
  },[]);

  const getProducts=()=>{
    let params = {
      limit: 2,
      page: page,
      name: searchText
    }
    dispatch(Actions.list(params));
  }

  const deleteItem=(item)=>{
    dispatch(Actions.deleteListItem(item));
  }

  const search =(e)=>{
    const searchText = e?.target?.value;
    setSearch(searchText);
    setPage(1);
    if(e.key === "Enter"){
     getProducts();
    }
    else{
      if(e?.target?.value === ""){
       getProducts();
      }
    }
  }

  return (
    <React.Fragment>
      <Header/>
      <main>
        <div className={classes.heroContent}>
          <Container maxWidth="sm">
            <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom>
              DO AQUA FISHES
            </Typography>
            <Typography variant="h5" align="center" color="textSecondary" paragraph>
             Wide variety of energetic, stunning, and healthy ornamental fishes.
            </Typography>
          </Container>
        </div>

        <Container className={classes.cardGrid} maxWidth="md">
          <div className="d-flex search-container">

            <div className={`search col-4 ${classes.search}`}>
                <div className={classes.searchIcon}>
                  <SearchIcon />
                </div>
                <InputBase
                  placeholder="Search…"
                  classes={{
                    root: classes.inputRoot,
                    input: classes.inputInput,
                  }}
                  inputProps={{ 'aria-label': 'search' }}
                  onKeyDown={(e)=> search(e)}
                  onChange={(e)=> search(e)}
                  />
                  </div>
            <div className="col-8 add-button">

              <a href={`/products/add-product`}>
                <Button size="large" variant="contained" color="primary" className="float-right">
                  Add New
                </Button>
              </a>
              </div>
              </div>
          <Grid container spacing={4}>
            {productList?.data?.map((prod) => (
              <Grid item key={prod.prod_id} xs={12} sm={6} md={4}>
                <Card className={classes.prod}>
                  <CardMedia
                    className={classes.cardMedia}
                    image={prod?.image ? prod.image : defaultImage}
                    title="Image title"
                  />
                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                      {prod?.name}
                    </Typography>
                    <Typography gutterBottom variant="h6" component="h2">
                      Rs.{prod?.price}/-
                    </Typography>
                    <Typography>
                      {prod.description}
                    </Typography>
                    <br/>
                    <Typography gutterBottom>
                      {prod.quantity} stocks left.
                    </Typography>
                  </CardContent>
                  <CardActions>
                      <a href={`/products/${prod.prod_id}`}>
                        <Button size="small" variant="contained" color="primary">
                          Edit
                        </Button>
                      </a>
                      <Button onClick={()=> deleteItem(prod)} variant="contained" size="small" color="secondary">
                        Delete
                      </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
            <Pagination className="pagination col" count={productList?.totalPages} page={page} variant="outlined" shape="rounded" color="primary" onChange={handleChange} />
            {productList?.totalCount == 0 && <div className="empty"> 
              <h5>There are no items.</h5>
            </div> }
          </Grid>
        </Container>
      </main>
      {/* Footer */}
      <footer className={classes.footer}>
        <Typography variant="h6" align="center" gutterBottom>
          Do Aqua Fishes
        </Typography>
        <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
          Wide variety of ornamental fish collection!
        </Typography>
        <Copyright />
      </footer>
      {/* End footer */}
    </React.Fragment>
  );
}
