import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import { Container, Form, Col} from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import './ProductList.css';
import { Actions } from '../actions';

const AddProduct = ({match}) => {
    const dispatch = useDispatch();
    const state = useSelector((state)=> state.list);
    const id = parseInt(match.params.id);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
      const form = event.currentTarget;
      console.log();
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
        alert();
      }
      else{
          alert("else");
      }

      setValidated(true);
    //   onSubmit();
    };

    useEffect(()=>{
        if( Number.isInteger(id) ){
            dispatch(Actions.getItem(id));
        }
    },[id]);

    useEffect(()=>{
        const { name, description, quantity, price, image } = state.details;
        setName(name);
        setDescription(description);
        setQuantity(quantity);
        setPrice(price);
        setImage(image);
    },[state])

    const onSubmit = () =>{
        let params = {
            name,
            description,
            quantity: parseInt(quantity),
            price: parseInt(price),
            image
        }
        //API Call
        id ?
        dispatch(Actions.updateItem(id, params))
        : 
        dispatch(Actions.addListItem(params))
    }

    console.log(id);
    console.log(Number.isInteger(id));
    return (
        <>
            <Header/>
            <Container className="input-container">
                <a href="/">
                    <Button className="go-back" size="small" color="inherit"  >
                        GO BACK
                    </Button>   
                </a>
                {/* <Form>
                    <Form.Group aria-required>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" value={name} placeholder="Enter name" onChange={(e)=>setName(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" value={description} placeholder="Enter description" onChange={(e)=>setDescription(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control value={quantity} placeholder="Enter quantity" onChange={(e)=>setQuantity(e.target.value)} />
                    </Form.Group>
                    <Form.Group aria-required>
                        <Form.Label>Price</Form.Label>
                        <Form.Control value={price} placeholder="Enter price" onChange={(e)=>setPrice(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.File id="exampleFormControlFile1" label="Image of product" />
                    </Form.Group>
                    <Button size="large" variant="contained" color="primary" onClick={onSubmit}>
                        { Number.isInteger(id) ? 'UPDATE' : 'ADD'}
                    </Button>
                </Form> */}

<Form noValidate validated={validated} onSubmit={handleSubmit}>
      <Form.Row>
        <Form.Group as={Col} md="6">
          <Form.Label>Name</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="Name"
            value={name} onChange={(e)=>setName(e.target.value)}
          />
        </Form.Group>
      </Form.Row>
      <Form.Row>
        <Form.Group as={Col} md="6">
          <Form.Label>Description</Form.Label>
          <Form.Control type="text" placeholder="Description" value={description} onChange={(e)=>setDescription(e.target.value)}/>
        </Form.Group>
        </Form.Row>
        <Form.Row>
        <Form.Group as={Col} md="6">
          <Form.Label>Quantity</Form.Label>
          <Form.Control type="text" placeholder="Quantity" required type="number" maxLength="3" value={quantity} onChange={(e)=>setQuantity(e.target.value)}/>
          <Form.Control.Feedback type="invalid">
            Please provide a valid quantity.
          </Form.Control.Feedback>
        </Form.Group>
      </Form.Row>
       <Form.Row>
        <Form.Group as={Col} md="6">
          <Form.Label>Price</Form.Label>
          <Form.Control type="text" placeholder="Price" required type="number" maxLength="4" value={price} onChange={(e)=>setPrice(e.target.value)}/>
          <Form.Control.Feedback type="invalid">
            Please provide a valid price.
          </Form.Control.Feedback>
        </Form.Group>
      </Form.Row>
       <Form.Row>
        <Form.Group as={Col} md="6">
          <Form.File
              className="position-relative"
              required
              name="file"
              label="Image"
              id="validationFormik107"
              feedbackTooltip
            />
        </Form.Group>
      </Form.Row>
      <Button type="submit" variant="contained" color="primary"> { Number.isInteger(id) ? 'UPDATE' : 'ADD'}</Button>
    </Form>
            </Container>
        </>
    );
};

export default AddProduct;
