import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import { Container, Form} from 'react-bootstrap';
import Button from '@material-ui/core/Button';
import './ProductList.css';
import { Actions } from '../actions';

const AddProduct = ({match}) => {
    const dispatch = useDispatch();
    const state = useSelector((state)=> state.list);
    const id = match.params.id;
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');

    useEffect(()=>{
        if( Number.isInteger(id) ){
            dispatch(Actions.getItem(id));
        }
    },[id]);

    useEffect(()=>{
        const { name, description, quantity, price, image } = state.details;
        setName(name);
        setDescription(description);
        setQuantity(quantity);
        setPrice(price);
        setImage(image);
    },[state])

    const onSubmit = () =>{
        let params = {
            name,
            description,
            quantity: parseInt(quantity),
            price: parseInt(price),
            image
        }
        //API Call
        id ?
        dispatch(Actions.updateItem(id, params))
        : 
        dispatch(Actions.addListItem(params))
    }

    log
    return (
        <>
            <Header/>
            <Container className="input-container">
                <a href="/">
                    <Button className="go-back" size="small" color="inherit"  >
                        GO BACK
                    </Button>   
                </a>
                <Form>
                    <Form.Group aria-required>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" value={name} placeholder="Enter name" onChange={(e)=>setName(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" value={description} placeholder="Enter description" onChange={(e)=>setDescription(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control value={quantity} placeholder="Enter quantity" onChange={(e)=>setQuantity(e.target.value)} />
                    </Form.Group>
                    <Form.Group aria-required>
                        <Form.Label>Price</Form.Label>
                        <Form.Control value={price} placeholder="Enter price" onChange={(e)=>setPrice(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.File id="exampleFormControlFile1" label="Image of product" />
                    </Form.Group>
                    <Button size="large" variant="contained" color="primary" onClick={onSubmit}>
                        { id  ? 'UPDATE' : 'ADD'}
                    </Button>
                </Form>
            </Container>
        </>
    );
};

export default AddProduct;
