import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import { ListGroup, ListGroupItem, Card, } from 'react-bootstrap';

import {Actions} from '../actions';
import './ProductList.css';

const defaultImage = "data:image/png;base64,";

const ProductList = () => {
  const dispatch = useDispatch();
  const productList = useSelector(state => state.list.list);

  useEffect(()=>{
    console.log("started");
    dispatch(Actions.list());
  },[]);

  const deleteItem=(item)=>{
    dispatch(Actions.deleteListItem(item));
  }

  return (
      <div className="main-container">
      {/* Header */}
      <Header/>
      <div className="container sub-container">
      <a href="/add-product" className="btn btn-primary float-right">Add New</a>
      {/* List of products */}
      <div className="row container-fluid">
      {
        productList.map((prod, index)=>{
          return(
            <div key={index} className="card-container">
              <img src={prod.image ?
                 prod.image : 
                 defaultImage} className="thumbnail" />
              <div className="body-container">
                <div className="row card-title-container"> 
                  <h5 className="card-title col-7">{prod.name}</h5>
                  <h6 className="card-text col-5 price">Rs.{prod.price}</h6>
                  <p className="card-text">{prod.description}</p>
                  <p className="card-text">Only {prod.quantity} left.</p>
                </div>
                <div className="col" style={{borderTopWidth: "1px solid black", borderTop: "1px solid black"}}>
                  <a href={`/products/${prod.prod_id}`} className="btn btn-primary button col-5" >Edit
                  </a>
                  <p className="btn btn-danger col-5 float-right" onClick={()=>deleteItem(prod)}>Delete</p>
                </div>
              </div>
            </div>
          )
        })
      }
      </div>
      </div>
      </div>

    );
};


export default ProductList;
