import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import {Actions} from '../actions';
import './ProductList.css';

const ProductList = () => {
  const dispatch = useDispatch();
  const productList = useSelector(state => state.list.list);

  useEffect(()=>{
    console.log("started");
    dispatch(Actions.list());
  },[]);

  const deleteItem=(id)=>{
    dispatch(Actions.deleteListItem(id));
  }

  return (
      <div className="main-container">
      {/* Header */}
      <Header/>
      <div className="container-fluid sub-container">
      <a href="#" className="btn btn-primary add-new" style={{float: "right"}} >Add New</a>
      {/* List of products */}
      <div className="row container-fluid">
      {
        productList.map((prod, index)=>{
          return(
            <div key={index} className="card" >
              <img src={prod.image != "" ?
                 prod.image : 
                 defaultImage} className="card-img-top thumbnail" />
              <div className="card-body">
                <div className="row card-title-container"> 
                <h5 className="card-title col-7">{prod.name}</h5>
                <h6 className="card-text col-5 price">Rs.{prod.price}</h6>
                  </div>
                <p className="card-text">{prod.description}</p>
                <p className="card-text">Only {prod.quantity} left.</p>
                <div className="col">
                <p className="btn btn-primary button col-5" >Edit</p>
                <p className="btn btn-danger col-5" style={{float: "right"}} onClick={()=>deleteItem(prod.prod_id)}>Delete</p>
                </div>
              </div>
            </div>
          )
        })
      }
      </div>
      </div>
      </div>

    );
};


export default ProductList;
