import * as TYPES from '../constants';

const initialState = {
    list: []
}
export default function reducer(state = initialState, action: any) {
    switch (action.type) {
        case TYPES.GET_LIST:
            return { ...state };
        case TYPES.GET_LIST_SUCCESS: {
            return {
                ...state,
                list: action.payload.data
            }
        }
        case TYPES.DELETE_LIST_ITEM_SUCCESS:{
            console.log(state.list);
            // const checkIndex=(item: any)=>{
            //     return item.prod_id == action.payload.prod_id
            // }
            const index = state.list.indexOf(action.payload);
            const updatedList = state.list.splice(index, 1);
            console.log(index, updatedList);

            return{
                ...state,
                // list: updatedList
            }
        }
        default:
            return state;
    }
};
