import * as TYPES from '../constants';

const initialState = {
    list: []
}
export default function reducer(state = initialState, action: any) {
    switch (action.type) {
        case TYPES.GET_LIST:
            return { ...state };
        case TYPES.GET_LIST_SUCCESS: {
            return {
                ...state,
                list: action.payload.data
            }
        }
        case TYPES.DELETE_LIST_ITEM_SUCCESS:{
            const index = state.list.findIndex(action.payload);
            const updatedList = state.list.splice(index, 1);
            console.log();

            return{
                ...state,
                list: updatedList
            }
        }
        default:
            return state;
    }
};
