import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../components/Header';
import { Button, Container, Form} from 'react-bootstrap';
import './ProductList.css';
import { Actions } from '../actions';

const AddProduct = ({match}) => {
    const dispatch = useDispatch();
    const state = useSelector((state)=> state.list);
    const id = match.params.id;
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [quantity, setQuantity] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');

    useEffect(()=>{
        if( id !== undefined){
            dispatch(Actions.getItem(id));
        }
    },[id]);

    useEffect(()=>{
        const { name, description, quantity, price, image } = state.details;
        setName(name);
        setDescription(description);
        setQuantity(quantity);
        setPrice(price);
        setImage(image);
    },[state])

    const onSubmit = () =>{
        let params = {
            name,
            description,
            quantity: parseInt(quantity),
            price: parseInt(price),
            image
        }
        console.log({params});
        //API Call
        dispatch(Actions.addListItem(params));
    }

    return (
        <>
            <Header/>
            <Container className="input-container">
                <Form>
                    <Form.Group aria-required>
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" value={name} placeholder="Enter name" onChange={(e)=>setName(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" value={description} placeholder="Enter description" onChange={(e)=>setDescription(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Quantity</Form.Label>
                        <Form.Control value={quanti} placeholder="Enter quantity" onChange={(e)=>setQuantity(e.target.value)} />
                    </Form.Group>
                    <Form.Group aria-required>
                        <Form.Label>Price</Form.Label>
                        <Form.Control placeholder="Enter price" onChange={(e)=>setPrice(e.target.value)} />
                    </Form.Group>
                    <Form.Group>
                        <Form.File id="exampleFormControlFile1" label="Image of product" />
                    </Form.Group>
                    <Button variant="primary" onClick={onSubmit}>
                        Add
                    </Button>
                </Form>
            </Container>
        </>
    );
};

export default AddProduct;
